// Hotel locations for dropdown
const hotelLocations = [
    { name: 'Rampur', slug: 'rampur' },
    { name: 'Delhi', slug: 'delhi' },
    { name: 'Jaipur', slug: 'jaipur' },
    { name: 'Chandigarh', slug: 'chandigarh' },
    { name: 'Agra', slug: 'agra' },
    { name: 'Udaipur', slug: 'udaipur' },
    { name: 'Mumbai', slug: 'mumbai' },
    { name: 'Goa', slug: 'goa' },
]

const menuItems = [
    { name: 'About Us', href: '#about' },
    { name: 'Contact Us', href: '#contact' },
]

const HeroHeader = () => {
    const [menuState, setMenuState] = React.useState(false)
    const [scrolled, setScrolled] = React.useState(false)
    const [hotelsDropdownOpen, setHotelsDropdownOpen] = React.useState(false)
    const { scrollYProgress } = useScroll()
    const dropdownRef = React.useRef<HTMLLIElement>(null)

    React.useEffect(() => {
        const unsubscribe = scrollYProgress.on('change', (latest) => {
            setScrolled(latest > 0.05)
        })
        return () => unsubscribe()
    }, [scrollYProgress])

    // Close dropdown when clicking outside
    React.useEffect(() => {
        const handleClickOutside = (event: MouseEvent) => {
            if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
                setHotelsDropdownOpen(false)
            }
        }
        document.addEventListener('mousedown', handleClickOutside)
        return () => document.removeEventListener('mousedown', handleClickOutside)
    }, [])

    return (
        <header>
            <nav
                data-state={menuState && 'active'}
                className="group fixed z-20 w-full pt-2">
                <div className={cn('mx-auto max-w-7xl rounded-3xl px-6 transition-all duration-300 lg:px-12', scrolled && 'bg-primary/95 backdrop-blur-2xl')}>
                    <motion.div
                        key={1}
                        className={cn('relative flex flex-wrap items-center justify-between gap-6 py-3 duration-200 lg:gap-0 lg:py-6', scrolled && 'lg:py-4')}>
                        {/* Logo */}
                        <Link
                            href="/"
                            aria-label="home"
                            className="flex items-center space-x-2 z-10">
                            <Logo />
                        </Link>

                        {/* Mobile Menu Button */}
                        <button
                            onClick={() => setMenuState(!menuState)}
                            aria-label={menuState == true ? 'Close Menu' : 'Open Menu'}
                            className="relative z-20 -m-2.5 -mr-4 block cursor-pointer p-2.5 lg:hidden">
                            <Menu className="group-data-[state=active]:rotate-180 group-data-[state=active]:scale-0 group-data-[state=active]:opacity-0 m-auto size-6 duration-200" />
                            <X className="group-data-[state=active]:rotate-0 group-data-[state=active]:scale-100 group-data-[state=active]:opacity-100 absolute inset-0 m-auto size-6 -rotate-180 scale-0 opacity-0 duration-200" />
                        </button>

                        {/* Desktop Menu Items */}
                        <div className="hidden lg:flex lg:items-center lg:gap-8 lg:flex-1 lg:justify-center">
                            <ul className="flex gap-8 text-sm items-center">
                                {/* Our Hotels Dropdown */}
                                <li className="relative" ref={dropdownRef}>
                                    <button
                                        onClick={() => setHotelsDropdownOpen(!hotelsDropdownOpen)}
                                        className="text-primary-foreground hover:text-highlight flex items-center gap-1 duration-150"
                                    >
                                        <span>Our Hotels</span>
                                        <ChevronDown className={`w-4 h-4 transition-transform duration-200 ${hotelsDropdownOpen ? 'rotate-180' : ''}`} />
                                    </button>
                                    {hotelsDropdownOpen && (
                                        <div className="absolute top-full left-0 mt-2 w-48 bg-primary-foreground rounded-xl shadow-xl border border-border overflow-hidden z-50">
                                            <ul className="py-2">
                                                {hotelLocations.map((location, index) => (
                                                    <li key={index}>
                                                        <Link
                                                            href={`/hotels/${location.slug}`}
                                                            className="block px-4 py-2 text-sm text-primary hover:bg-surface transition-colors duration-150"
                                                            onClick={() => setHotelsDropdownOpen(false)}
                                                        >
                                                            {location.name}
                                                        </Link>
                                                    </li>
                                                ))}
                                            </ul>
                                        </div>
                                    )}
                                </li>
                                {/* Other Menu Items */}
                                {menuItems.map((item, index) => (
                                    <li key={index}>
                                        <Link
                                            href={item.href}
                                            className="text-primary-foreground hover:text-highlight block duration-150">
                                            <span>{item.name}</span>
                                        </Link>
                                    </li>
                                ))}
                            </ul>
                        </div>

                        {/* CTA Buttons for Desktop - Right Side */}
                        <div className="hidden lg:flex lg:items-center lg:gap-3">
                            <Button
                                asChild
                                size="sm"
                                className="h-9 rounded-full px-5 bg-primary-foreground text-primary hover:bg-surface font-semibold"
                            >
                                <Link href="#book-now">
                                    Book Now
                                </Link>
                            </Button>
                            <Button
                                asChild
                                size="sm"
                                variant="outline"
                                className="h-9 rounded-full px-5 border-2 border-primary-foreground text-primary-foreground hover:bg-primary-foreground/10 font-semibold"
                            >
                                <Link href="#partner-with-us">
                                    Partner With Us
                                </Link>
                            </Button>
                        </div>

                        <div className="bg-primary group-data-[state=active]:block lg:group-data-[state=active]:flex mb-6 hidden w-full flex-wrap items-center justify-end space-y-8 rounded-3xl border border-border p-6 shadow-2xl shadow-primary/20 md:flex-nowrap lg:m-0 lg:flex lg:w-fit lg:gap-6 lg:space-y-0 lg:border-transparent lg:bg-transparent lg:p-0 lg:shadow-none">
                            <div className="lg:hidden w-full">
                                <ul className="space-y-6 text-base">
                                    {/* Our Hotels Dropdown for Mobile */}
                                    <li>
                                        <button
                                            onClick={() => setHotelsDropdownOpen(!hotelsDropdownOpen)}
                                            className="text-primary-foreground hover:text-highlight flex items-center gap-2 w-full duration-150"
                                        >
                                            <span>Our Hotels</span>
                                            <ChevronDown className={`w-4 h-4 transition-transform duration-200 ${hotelsDropdownOpen ? 'rotate-180' : ''}`} />
                                        </button>
                                        {hotelsDropdownOpen && (
                                            <ul className="mt-3 ml-4 space-y-3">
                                                {hotelLocations.map((location, index) => (
                                                    <li key={index}>
                                                        <Link
                                                            href={`/hotels/${location.slug}`}
                                                            className="text-primary-foreground/80 hover:text-highlight block duration-150"
                                                            onClick={() => {
                                                                setHotelsDropdownOpen(false)
                                                                setMenuState(false)
                                                            }}
                                                        >
                                                            {location.name}
                                                        </Link>
                                                    </li>
                                                ))}
                                            </ul>
                                        )}
                                    </li>
                                    {/* Other Menu Items */}
                                    {menuItems.map((item, index) => (
                                        <li key={index}>
                                            <Link
                                                href={item.href}
                                                className="text-[#FAFCFB] hover:text-[#D8E6E2] block duration-150"
                                                onClick={() => setMenuState(false)}
                                            >
                                                <span>{item.name}</span>
                                            </Link>
                                        </li>
                                    ))}
                                </ul>
                                {/* CTA Buttons for Mobile */}
                                <div className="flex flex-col gap-3 mt-6">
                                    <Button
                                        asChild
                                        size="sm"
                                        className="w-full h-10 rounded-full bg-white text-[#0E553F] hover:bg-[#E8F0ED] font-semibold"
                                    >
                                        <Link href="#book-now" onClick={() => setMenuState(false)}>
                                            Book Now
                                        </Link>
                                    </Button>
                                    <Button
                                        asChild
                                        size="sm"
                                        variant="outline"
                                        className="w-full h-10 rounded-full border-2 border-white text-white hover:bg-white/10 font-semibold"
                                    >
                                        <Link href="#partner-with-us" onClick={() => setMenuState(false)}>
                                            Partner With Us
                                        </Link>
                                    </Button>
                                </div>
                            </div>
                        </div>
                    </motion.div>
                </div>
            </nav>
        </header>
    )
}

